import java.util.List;import java.util.Map;public class ShowProjectsController {  private final Session session;  private final TaskManSystem taskManSystem;  private Session getSession() {    return session;  }  private TaskManSystem getTaskManSystem() {    return taskManSystem;  }  public ShowProjectsController(Session session, TaskManSystem taskManSystem) {    this.session = session;    this.taskManSystem = taskManSystem;  }  public String showProject(String projectName) throws IncorrectPermissionException, ProjectNotFoundException {    if (getSession().getRole() != Role.PROJECTMANAGER) {      throw new IncorrectPermissionException();    }    return getTaskManSystem().showProject(projectName);  }  public String showTask(String projectName, String taskName) throws IncorrectPermissionException, ProjectNotFoundException, TaskNotFoundException {    if (getSession().getRole() != Role.PROJECTMANAGER) {      throw new IncorrectPermissionException();    }    return getTaskManSystem().showTask(projectName, taskName);  }  public List<Map.Entry<String, String>> getProjectNamesWithStatus() throws IncorrectPermissionException {    if (getSession().getRole() != Role.PROJECTMANAGER) {      throw new IncorrectPermissionException();    }    return getTaskManSystem().getProjectNamesWithStatus();  }  public boolean showProjectsPreconditions() {    return getSession().getRole() == Role.PROJECTMANAGER;  }}