package Application;import Domain.*;import Domain.TaskStates.TaskProxy;import java.util.Map;/** * Separates domain from UI for the showprojects use-case */public class ShowProjectsController {    private final SessionWrapper session;    private final TaskManSystem taskManSystem;    public ShowProjectsController(SessionWrapper session, TaskManSystem taskManSystem) {        this.session = session;        this.taskManSystem = taskManSystem;    }    private SessionWrapper getSession() {        return session;    }    private TaskManSystem getTaskManSystem() {        return taskManSystem;    }    public boolean showProjectsPreconditions() {        return getSession().getRoles() != null && getSession().getRoles().contains(Role.PROJECTMANAGER);    }    public TaskManSystemProxy getTaskManSystemData() throws IncorrectPermissionException {        if (!showProjectsPreconditions()) {            throw new IncorrectPermissionException("You must be logged in with the " + Role.PROJECTMANAGER + " role to call this function");        }        return getTaskManSystem().getTaskManSystemData();    }    public ProjectProxy getProjectData(String projectName) throws ProjectNotFoundException, IncorrectPermissionException {        if (!showProjectsPreconditions()) {            throw new IncorrectPermissionException("You must be logged in with the " + Role.PROJECTMANAGER + " role to call this function");        }        return getTaskManSystem().getProjectData(projectName);    }    public TaskProxy getTaskData(String projectName, String taskName) throws IncorrectPermissionException, ProjectNotFoundException, TaskNotFoundException {        if (!showProjectsPreconditions()) {            throw new IncorrectPermissionException("You must be logged in with the " + Role.PROJECTMANAGER + " role to call this function");        }        return getTaskManSystem().getTaskData(projectName, taskName);    }}